import React, { useState } from "react";
import { ListItem, ListItemText } from "@mui/material";
import StarBorderIcon from '@mui/icons-material/StarBorder';
import StarIcon from '@mui/icons-material/Star';

const CustomListItem = ({ workout, onClick }) => {
    const [isStarred, setIsStarred] = useState(false);

    const handleStarClick = () => {
        setIsStarred(!isStarred);
    };

    return (
        <ListItem style={styles.listItem} onClick={onClick}>
            <div style={styles.leftContainer}>
                <div style={styles.titleContainer}>
                    <span style={styles.title}>{workout.title}</span>
                </div>
                <ListItemText primary={workout.description} />
                <ListItemText secondary={`${workout.daysPerWeek} days per week`} />
            </div>
            <div style={styles.starContainer}>
                {isStarred ? (
                    <StarIcon style={styles.starIcon} onClick={handleStarClick} />
                ) : (
                    <StarBorderIcon style={styles.starIcon} onClick={handleStarClick} />
                )}
            </div>
            <div style={styles.imageContainer}>
                <img src={workout.image} alt={workout.title} style={styles.image} />
            </div>
        </ListItem>
    );
};

const styles = {
    listItem: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        position: 'relative', // Ensure positioning context
    },
    leftContainer: {
        display: 'flex',
        flexDirection: 'column',
    },
    titleContainer: {
        display: 'flex',
        alignItems: 'center',
    },
    title: {
        fontSize: 20,
    },
    starContainer: {
        position: 'absolute',
        top: '50%', // Center vertically
        right: 16, // Adjust as needed
        transform: 'translateY(-50%)', // Center vertically
    },
    starIcon: {
        cursor: 'pointer',
    },
    imageContainer: {
        flexShrink: 0,
        marginLeft: 16,
    },
    image: {
        width: 100,
        height: 100,
        borderRadius: 4,
    },
};

export default CustomListItem;
