import React from "react";
import { ListItem, ListItemText } from "@mui/material";
import StarBorderIcon from '@mui/icons-material/StarBorder';

const CustomListItem = ({ workout, onClick }) => {
    return (
        <ListItem style={styles.listItem} onClick={onClick}>
            <div style={styles.topLeftContainer}>
                <StarBorderIcon style={styles.starIcon} />
                <ListItemText primary={<span style={styles.title}>{workout.title}</span>} />
            </div>
            <div style={styles.bottomContainer}>
                <ListItemText primary={workout.description} />
                <ListItemText secondary={`${workout.daysPerWeek} days per week`} />
            </div>
            <div style={styles.imageContainer}>
                <img src={workout.image} alt={workout.title} style={styles.image} />
            </div>
        </ListItem>
    );
};

const styles = {
    listItem: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
    },
    topLeftContainer: {
        display: 'flex',
        flexDirection: 'column',
    },
    starIcon: {
        marginRight: 8,
    },
    title: {
        fontSize: 20, // Adjust as needed
    },
    bottomContainer: {
        flex: '1',
        display: 'flex',
        flexDirection: 'column',
    },
    imageContainer: {
        flexShrink: 0,
        marginLeft: 16,
    },
    image: {
        width: 100,
        height: 100,
        borderRadius: 4,
    },
};

export default CustomListItem;
